function addImage() {
    var amtImages = document.getElementById('amt-images')

    if (amtImages.value > 24){
        alert('Max amount of images is 24')
        return;
    }

    var imageList = ['image-div1','image-div2','image-div3','image-div4','image-div5','image-div6','image-div7','image-div8','image-div9','image-div10','image-div11','image-div12','image-div13','image-div14','image-div15','image-div16','image-div17','image-div18','image-div19','image-div20','image-div21','image-div22','image-div23','image-div24']
    
    for (let image = 0; image < amtImages.value ; image ++) {
        let imageId = document.getElementById(imageList[image])
        if (imageId.style.display == 'none') {
            imageId.style.display = 'grid';
        } 
    }

}



